    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        ButtonModel selection = buttonGroup1.getSelection();
        String name = jTextField1.getText();
        String pass = jPasswordField1.getText();
        if(selection.getMnemonic()==48){
            JOptionPane.showMessageDialog(this, "Bienvenido Coordinador " + name );
        }
        if(selection.getMnemonic()==49){
            JOptionPane.showMessageDialog(this, "Bienvenido Profesor " + name );
        }
        if(selection.getMnemonic()==50){
            JOptionPane.showMessageDialog(this, "Bienvenido Estudiante " + name );
        }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

